d\":1635703208,\"_links\":{\"self\":{\"href\":\"https://api.tvmaze.com/shows/13991\"},\"previousepisode\":{\"href\":\"https://api.tvmaze.com/episodes/644908\"}}}},{\"score\":0.6932127,\"show\":{\"id\":32965,\"url\":\"https://www.tvmaze.com/shows/32965/murder-city\",\"name\":\"Murder City\",\"type\":\"Scripted\",\"language\":\"English\",\"genres\":[\"Drama\",\"Crime\"],\"status\":\"Ended\",\"runtime\":90,\"averageRuntime\":90,\"premiered\":\"2004-03-17\",\"ended\":\"2006-04-26\",\"officialSite\":null,\"schedule\":{\"time\":\"21:00\",\"days\":[\"Thursday\"]},\"rating\":{\"average\":null},\"weight\":39,\"network\":{\"id\":35,\"name\":\"ITV\",\"country\":{\"name\":\"United Kingdom\",\"code\":\"GB\",\"timezone\":\"Europe/London\"}},\"webChannel\":null,\"dvdCountry\":null,\"externals\":{\"tvrage\":null,\"thetvdb\":74171,\"imdb\":null},\"image\":{\"medium\":\"https://static.tvmaze.com/uploads/images/medium_portrait/132/332414.jpg\",\"original\":\"https://static.tvmaze.com/uploads/images/original_untouched/132/332414.jpg\"},\"summary\":\"<p><b>Murder City</b> is a police drama with a mismatched duo at its heart. The pair solve challenging homicide cases with very different but critical investigation techniques.</p><p>Despite their polar opposite personalities, methodical DI Susan Alembic (Amanda Donohoe) and unorthodox DS Luke Stone (Kris Marshall) work as an effective team solving complex crimes.</p>\",\"updated\":1591325351,\"_links\":{\"self\":{\"href\":\"https://api.tvmaze.com/shows/32965\"},\"previousepisode\":{\"href\":\"https://api.tvmaze.com/episodes/1342727\"}}}},{\"score\":0.6906339,\"show\":{\"id\":57603,\"url\":\"https://www.tvmaze.com/shows/57603/murder-island\",\"name\":\"Murder Island\",\"type\":\"Reality\",\"language\":\"English\",\"genres\":[],\"status\":\"Running\",\"runtime\":null,\"averageRuntime\":61,\"premiered\":\"2021-10-05\",\"ended\":null,\"officialSite\":\"https://www.channel4.com/programmes/murder-island\",\"schedule\":{\"time\":\"\",\"days\":[\"